exports.isOp = function (info) {

	var channels = info.channels;
	var flag = false;

	for (var i in channels) {
		if (channels.hasOwnProperty(i)) {

			if (channels[i][0] == "@") {
				flag = true;
			}

		}
	}

	return flag;

};

exports.rint = function (min, max) {
	return Math.floor(Math.random() * (max - min + 1)) + min;
};

